import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import {
    Box,
    Grid,
    Card,
    CardContent,
    Typography,
    CircularProgress,
    Chip,
    IconButton,
    Alert,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    Paper,
} from '@mui/material';
import {
    School as SchoolIcon,
    Class as ClassIcon,
    Assignment as AssignmentIcon,
    CheckCircle as CheckCircleIcon,
    Schedule as ScheduleIcon,
    ArrowForward as ArrowForwardIcon,
} from '@mui/icons-material';
import { format } from 'date-fns';
import { RootState } from '../store';
import { dashboardService } from '../services/dashboardService';
import { DashboardData } from '../interfaces/dashboard';

const StatCard: React.FC<{
    title: string;
    value: number;
    icon: React.ReactNode;
    color: string;
}> = ({ title, value, icon, color }) => (
    <Card>
        <CardContent>
            <Box display="flex" alignItems="center" justifyContent="space-between">
                <Box>
                    <Typography color="textSecondary" gutterBottom>
                        {title}
                    </Typography>
                    <Typography variant="h4">{value}</Typography>
                </Box>
                <Box
                    sx={{
                        backgroundColor: color,
                        borderRadius: '50%',
                        width: 48,
                        height: 48,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        color: 'white',
                    }}
                >
                    {icon}
                </Box>
            </Box>
        </CardContent>
    </Card>
);

const Dashboard: React.FC = () => {
    const navigate = useNavigate();
    const user = useSelector((state: RootState) => state.auth.user);
    const [dashboardData, setDashboardData] = useState<DashboardData | null>(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchDashboardData = async () => {
            try {
                setLoading(true);
                let data: DashboardData;

                if (user?.roles.includes('ADMIN')) {
                    data = await dashboardService.getDashboardData();
                } else if (user?.roles.includes('TEACHER')) {
                    data = await dashboardService.getTeacherDashboard(user.id);
                } else {
                    data = await dashboardService.getStudentDashboard(user!.id);
                }

                setDashboardData(data);
                setError(null);
            } catch (err) {
                setError('Failed to fetch dashboard data');
                console.error('Error fetching dashboard data:', err);
            } finally {
                setLoading(false);
            }
        };

        if (user) {
            fetchDashboardData();
        }
    }, [user]);

    if (loading) {
        return (
            <Box display="flex" justifyContent="center" alignItems="center" minHeight="80vh">
                <CircularProgress />
            </Box>
        );
    }

    if (!dashboardData) {
        return null;
    }

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" gutterBottom>
                Welcome back, {user?.firstName}!
            </Typography>

            {error && (
                <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
                    {error}
                </Alert>
            )}

            <Grid container spacing={3}>
                {/* Stats Cards */}
                <Grid item xs={12} sm={6} md={3}>
                    <StatCard
                        title="Total Courses"
                        value={dashboardData.stats.totalCourses}
                        icon={<SchoolIcon />}
                        color="#1976d2"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                    <StatCard
                        title="Total Classrooms"
                        value={dashboardData.stats.totalClassrooms}
                        icon={<ClassIcon />}
                        color="#2e7d32"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                    <StatCard
                        title="Active Tasks"
                        value={dashboardData.stats.activeTasks}
                        icon={<AssignmentIcon />}
                        color="#ed6c02"
                    />
                </Grid>
                <Grid item xs={12} sm={6} md={3}>
                    <StatCard
                        title="Completed Tasks"
                        value={dashboardData.stats.completedTasks}
                        icon={<CheckCircleIcon />}
                        color="#9c27b0"
                    />
                </Grid>

                {/* Recent Courses */}
                <Grid item xs={12} md={6}>
                    <Card>
                        <CardContent>
                            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                                <Typography variant="h6">Recent Courses</Typography>
                                <IconButton onClick={() => navigate('/courses')}>
                                    <ArrowForwardIcon />
                                </IconButton>
                            </Box>
                            <TableContainer component={Paper} variant="outlined">
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Title</TableCell>
                                            <TableCell>Status</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {dashboardData.courses.slice(0, 5).map((course) => (
                                            <TableRow
                                                key={course.id}
                                                hover
                                                onClick={() => navigate(`/courses/${course.id}`)}
                                                sx={{ cursor: 'pointer' }}
                                            >
                                                <TableCell>{course.title}</TableCell>
                                                <TableCell>
                                                    <Chip
                                                        label={course.status}
                                                        color={
                                                            course.status === 'ACTIVE'
                                                                ? 'success'
                                                                : 'default'
                                                        }
                                                        size="small"
                                                    />
                                                </TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                </Grid>

                {/* Recent Classrooms */}
                <Grid item xs={12} md={6}>
                    <Card>
                        <CardContent>
                            <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
                                <Typography variant="h6">Recent Classrooms</Typography>
                                <IconButton onClick={() => navigate('/classrooms')}>
                                    <ArrowForwardIcon />
                                </IconButton>
                            </Box>
                            <TableContainer component={Paper} variant="outlined">
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Name</TableCell>
                                            <TableCell>Description</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {dashboardData.classrooms.slice(0, 5).map((classroom) => (
                                            <TableRow
                                                key={classroom.id}
                                                hover
                                                onClick={() => navigate(`/classrooms/${classroom.id}`)}
                                                sx={{ cursor: 'pointer' }}
                                            >
                                                <TableCell>{classroom.name}</TableCell>
                                                <TableCell>{classroom.description}</TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                </Grid>

                {/* Upcoming Deadlines */}
                <Grid item xs={12}>
                    <Card>
                        <CardContent>
                            <Box display="flex" alignItems="center" mb={2}>
                                <ScheduleIcon sx={{ mr: 1, color: 'warning.main' }} />
                                <Typography variant="h6">Upcoming Deadlines</Typography>
                            </Box>
                            <TableContainer component={Paper} variant="outlined">
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Task</TableCell>
                                            <TableCell>Course</TableCell>
                                            <TableCell>Due Date</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {dashboardData.stats.upcomingDeadlines.map((deadline) => (
                                            <TableRow
                                                key={deadline.taskId}
                                                hover
                                                onClick={() => navigate(`/tasks/${deadline.taskId}`)}
                                                sx={{ cursor: 'pointer' }}
                                            >
                                                <TableCell>{deadline.title}</TableCell>
                                                <TableCell>{deadline.courseTitle}</TableCell>
                                                <TableCell>
                                                    {format(new Date(deadline.dueDate), 'PPp')}
                                                </TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        </CardContent>
                    </Card>
                </Grid>
            </Grid>
        </Box>
    );
};

export default Dashboard;
